import React, { useEffect, useState } from "react";
import axios from "axios";

const ManagerDashboard = () => {
  const [ptoRequests, setPtoRequests] = useState([]);

  useEffect(() => {
    const fetchPtoRequests = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/pto-requests", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPtoRequests(response.data);
      } catch (error) {
        console.error(error.response?.data?.message || "An error occurred");
      }
    };

    fetchPtoRequests();
  }, []);

  return (
    <div>
      <h2>Manager Dashboard</h2>
      <h3>Pending PTO Requests</h3>
      {ptoRequests.length === 0 ? (
        <p>No pending PTO requests.</p>
      ) : (
        <ul>
          {ptoRequests.map((request) => (
            <li key={request.id}>
              <p>
                <strong>User:</strong> {request.user.name} ({request.user.email})
              </p>
              <p>
                <strong>Type:</strong> {request.leaveType}
              </p>
              <p>
                <strong>Dates:</strong> {request.startDate} to {request.endDate}
              </p>
              <p>
                <strong>Status:</strong> {request.status}
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ManagerDashboard;
