import React, { useEffect, useState } from "react";
import axios from "axios";

const EmployeeDashboard = () => {
  const [ptoRequests, setPtoRequests] = useState([]);

  useEffect(() => {
    const fetchPtoRequests = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/pto-requests/my-requests", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPtoRequests(res.data);
      } catch (err) {
        console.error(err.response?.data?.message || "An error occurred");
      }
    };

    fetchPtoRequests();
  }, []);

  return (
    <div>
      <h2>Employee Dashboard</h2>
      <ul>
        {ptoRequests.map((request) => (
          <li key={request.id}>
            <p>Type: {request.leaveType}</p>
            <p>Status: {request.status}</p>
            {request.managerComment && <p>Manager Comment: {request.managerComment}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeDashboard;
